import {React,useState} from 'react';
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import KeyboardArrowUpIcon from '@material-ui/icons/KeyboardArrowUp';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import Collapse from '@material-ui/core/Collapse';
import IconButton from '@material-ui/core/IconButton';
import TableCell from '@material-ui/core/TableCell';
import { makeStyles } from '@material-ui/core/styles';
import TableRow from '@material-ui/core/TableRow';
import TableHead from '@material-ui/core/TableHead';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import PropTypes from 'prop-types';
import NotificationsActiveTwoToneIcon from '@material-ui/icons/NotificationsActiveTwoTone';
import NotificationsOffTwoToneIcon from '@material-ui/icons/NotificationsOffTwoTone';
import EditIcon from "@material-ui/icons/EditOutlined";
import DoneIcon from "@material-ui/icons/DoneAllTwoTone";
import RevertIcon from "@material-ui/icons/NotInterestedOutlined";
import axios from 'axios';



const useRowStyles = makeStyles({
  root: {
    '& > *': {
      borderBottom: 'unset',
    },
  },
});

function AddRowsData(props) {
  const { row } = props;
  const [open, setOpen] = useState(false);
  const classes = useRowStyles();
  const [isAlarmOff, setAlarmOff]  = useState(true);
 const [isPrice,setIsPrice] = useState(' ');
  // const onToggleEditMode = id => {
  //   setRows(state => {
  //     return rows.map(row => {
  //       if (row.id === id) {
  //         return { ...row, isEditMode: !row.isEditMode };
  //       }
  //       return row;
  //     });
  //   });
  // };

  axios.get('https://api.coingecko.com/api/v3/simple/price?', {
    params: {
      ids: 'cardano',
      vs_currencies: 'USD',

    }
  })
  .then(function (response) {
    setIsPrice(response?.data?.cardano?.usd);
  //  let cardanoPrice = response?.data?.cardano?.usd;
    console.log(response);
  })

  return (
    <>
      <TableRow className={classes.root} key={row.id}>
        <TableCell>

          <IconButton aria-label="expand row" size="small" onClick={() => setOpen(!open)}>
            {open ? <KeyboardArrowUpIcon /> : <KeyboardArrowDownIcon />}
          </IconButton>

        </TableCell>
        <TableCell component="th" scope="row">
          {/* {row.coinName} */}
          <Select>
            <MenuItem value='BTC'>Bitcoin</MenuItem>
            <MenuItem value='ETH'>Ethereum</MenuItem>
            <MenuItem value='ADA'>Cardano</MenuItem>
          </Select>
        </TableCell>
        <TableCell> 
          <IconButton
            aria-label="delete"
          // onClick={() => onToggleEditMode(row.id)}
          >
          <EditIcon />
          </IconButton> 
          {row.totalCoins}
          </TableCell>
        <TableCell> 
          <IconButton
            aria-label="delete"
          // onClick={() => onToggleEditMode(row.id)}
          >
          <EditIcon />
          </IconButton>  {row.totalInvestment}</TableCell>
        {/* <TableCell>{row.avgCost}</TableCell> */}
        <TableCell>{Math.round(row.totalInvestment / row.totalCoins) }</TableCell>
        {/* <TableCell>{row.currentPrice}</TableCell> */}
        <TableCell>{isPrice}</TableCell>
        {/* <TableCell>{row.totalReturn}</TableCell> */}
        <TableCell>{Math.round(row.totalCoins * row.currentPrice) - row.totalInvestment}</TableCell>
        <TableCell>
          {/* {row.exchange} */}
          <Select>
            <MenuItem value='enable'>Enable</MenuItem>
            <MenuItem value='disable'>Disable</MenuItem>
            <MenuItem value='pause'>Pause</MenuItem>
          </Select>
        </TableCell>
      </TableRow>
      <TableRow>
      {/* Details */}
        <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
          <Collapse in={open} timeout="auto" unmountOnExit>
            <Box margin={1}>
              <Typography variant="h6" gutterBottom component="div">
                Details
              </Typography>
              <Table size="small" aria-label="purchases">
                <TableHead>
                  <TableRow>
                    {/* <TableCell>Total Return ($)</TableCell> */}
                    <TableCell>Taxed</TableCell>
                    {/* <TableCell>Deposited ?</TableCell>
                    <TableCell>No Of Coins</TableCell>
                    <TableCell>Contract Period</TableCell>
                    <TableCell >APY</TableCell> */}
                    <TableCell>Net Profit&nbsp;($)</TableCell>
                    <TableCell align="right">Notification</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {/* Details Data */}
                  {row.details.map((detailsRow) => (
                    <TableRow key={detailsRow.taxed}>
                      <TableCell component="th" scope="row">
                        {detailsRow.taxed}
                      </TableCell>
                      {/* <TableCell>{detailsRow.deposit}</TableCell>
                      <TableCell>{detailsRow.noOfDepositCoins}</TableCell>
                      <TableCell>{detailsRow.contractPeriod}</TableCell> */}
                      {/* <TableCell>
                        {detailsRow.apy} */}
                        {/* {Math.round(detailsRow.amount * row.price * 100) / 100} */}
                      {/* </TableCell> */}
                      <TableCell >
                       {detailsRow.netProfit}
                      </TableCell>
                      <TableCell align="right">
                       {isAlarmOff ? <NotificationsActiveTwoToneIcon onClick={() => { setAlarmOff(!isAlarmOff) }}/> : <NotificationsOffTwoToneIcon onClick={() => { setAlarmOff(!isAlarmOff) }}/>}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </Box>
          </Collapse>
        </TableCell>
      </TableRow>
    </>
  );
}

AddRowsData.propTypes = {
  row: PropTypes.shape({
    totalCoin: PropTypes.number.isRequired,
    totalInvestment: PropTypes.number.isRequired,
    avgCost: PropTypes.number.isRequired,
    details: PropTypes.arrayOf(
      PropTypes.shape({
        amount: PropTypes.number.isRequired,
        customerId: PropTypes.string.isRequired,
        date: PropTypes.string.isRequired,
      }),
    ).isRequired,
    name: PropTypes.string.isRequired,
    currentPrice: PropTypes.number.isRequired,
    exchange: PropTypes.string.isRequired,
  }).isRequired,
};
export {AddRowsData};
